<template>
  <demo-section>
    <demo-block :title="t('basicUsage')">
      <van-signature
        v-model="value"
        :disabled="disabled"
        :line-width="lineWidth"
        :line-color="lineColor"
      />
    </demo-block>

    <van-button plain type="primary" @click="onChangeValue">初始化</van-button>
    <van-button plain type="primary" @click="onEmpty">清空</van-button>
    <van-button plain type="primary" @click="onSetLineWidth"
      >设置粗细</van-button
    >
    <van-button plain type="primary" @click="onSetLineColor"
      >设置颜色</van-button
    >
    <van-button plain type="primary" @click="disabled = !disabled"
      >设置{{ disabled ? '启用' : '禁用' }}</van-button
    >
  </demo-section>
</template>

<script>
export default {
  i18n: {
    'zh-CN': {},
    'en-US': {},
  },

  data() {
    return {
      value: '',
      disabled: false,
      lineWidth: 1,
      lineColor: '#000000',
    };
  },

  methods: {
    onEmpty() {
      this.value = '';
    },
    onChangeValue() {
      this.value =
        'data:image/png;base64,';
    },
    onSetLineWidth() {
      this.lineWidth = 5;
    },
    onSetLineColor() {
      this.lineColor = '#ff0000';
    },
  },
};
</script>

<style lang="less">
@import '../../style/var';

.demo-signature {
  .van-signature {
    box-sizing: border-box;
    width: 100%;
    height: 200px;
    margin-bottom: @padding-md;
    border: 1px solid #dcdee0;
    canvas.van-signature__node {
      width: 100%;
      height: 100%;
    }
  }
  .van-button {
    margin-right: @padding-md;
    margin-bottom: @padding-md;
  }
}
</style>
